mapper.select:
SELECT r.id AS id, r.sid AS sid, r.serial AS serial, date_format(r.rdate, '%s')||'000' AS rdate,
s.serial || '-' || s.name AS supply
FROM receipt r, supply s
WHERE r.sid = #{sid}
AND r.sid=s.id
AND date_format(r.rdate, '%Y') = #{year}

    AND date_format(r.rdate, '%m') = #{month}

ORDER BY rdate
