mapper.select:
SELECT *
FROM invoice_sup
WHERE sup_id = #{sup_id} AND year = #{year} AND month = #{month}

mapper.insert:
INSERT INTO invoice_sup (
sup_id, year, month, unit, date, total, rate
) VALUES(
#{sup_id},
#{year},
#{month},
#{unit},
#{date},
#{total},
#{rate}
)

    SELECT last_insert_id();
