mapper.select:
SELECT *
FROM remit_sup
WHERE sup_id = #{sup_id} AND year = #{year} AND month = #{month}

mapper.insert:
INSERT INTO remit_sup (
sup_id, year, month, unit, date, total, mode, note
) VALUES(
#{sup_id},
#{year},
#{month},
#{unit},
#{date},
#{total},
#{mode},
#{note}
)

    SELECT last_insert_id();
